import React from "react";
import pirateGirl from "./pirate_girl.png"; // Assurez-vous que le chemin est correct

// Données des agents IA avec un flag "available"
// Seul l'agent Pirate Girl est disponible (affiche une image) ; les autres affichent un bouton
const agentsData = [
  {
    id: 1,
    name: "Pirate Girl",
    image: pirateGirl,
    available: true,
  },
  {
    id: 2,
    name: "Agent Beta",
    available: false,
  },
  {
    id: 3,
    name: "Agent Gamma",
    available: false,
  },
  {
    id: 4,
    name: "Agent Delta",
    available: false,
  },
  {
    id: 5,
    name: "Agent Epsilon",
    available: false,
  },
  {
    id: 6,
    name: "Agent Zeta",
    available: false,
  },
  {
    id: 7,
    name: "Agent Eta",
    available: false,
  },
  {
    id: 8,
    name: "Agent Theta",
    available: false,
  },
  {
    id: 9,
    name: "Agent Iota",
    available: false,
  },
];

function AIAgents() {
  // Conteneur global (90% de la largeur de la page, centré)
  const containerStyle = {
    width: "90%",
    margin: "0 auto",
    padding: "20px",
  };

  // Grille à 3 colonnes avec un espacement constant
  const gridStyle = {
    display: "grid",
    gridTemplateColumns: "repeat(3, 1fr)",
    gap: "20px",
    marginTop: "20px",
  };

  // Style de la card avec dimensions fixes (hauteur, largeur automatique selon la grille)
  const cardStyle = {
    backgroundColor: "#f9f9f9",
    borderRadius: "8px",
    overflow: "hidden", // Pour respecter les coins arrondis
    boxShadow: "0 2px 8px rgba(0, 0, 0, 0.1)",
    width: "100%",
    height: "200px", // Hauteur fixe pour uniformiser les cards
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  };

  // Pour une image, on la fait remplir toute la card en gardant ses proportions
  const imageStyle = {
    width: "100%",
    height: "100%",
    objectFit: "cover",
    display: "block",
  };

  // Style du bouton, centré dans la card pour les agents non disponibles
  const buttonStyle = {
    padding: "10px 20px",
    fontSize: "1rem",
    border: "none",
    backgroundColor: "#e67e22",
    color: "#fff",
    borderRadius: "5px",
    cursor: "pointer",
  };

  // Style pour le nom de l'agent affiché en dessous de la card
  const nameStyle = {
    marginTop: "10px",
    fontSize: "1.2rem",
    fontWeight: "bold",
    textAlign: "center",
  };

  return (
    <div style={containerStyle}>
      <h1 style={{ textAlign: "center" }}>Catalogue des Agents IA</h1>
      <div style={gridStyle}>
        {agentsData.map((agent) => (
          <div key={agent.id} style={{ textAlign: "center" }}>
            <div style={cardStyle}>
              {agent.available ? (
                <img src={agent.image} alt={agent.name} style={imageStyle} />
              ) : (
                <button style={buttonStyle}>Add your Agent ...</button>
              )}
            </div>
            <div style={nameStyle}>{agent.name}</div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default AIAgents;
